//! Local primitives module for selendra-client
//!
//! Re-exports runtime `primitives` types generated by `subxt` from the chain metadata
//! and adds essential type aliases that are used throughout selendra-client.

pub use crate::selendra::api::runtime_types::primitives::*;

// Essential type aliases used throughout selendra-client
// These are not in runtime_types::primitives but are used extensively

/// The balance of an account.
pub type Balance = u128;

/// Index of a transaction in the chain.
pub type Nonce = u32;

/// The block number type used by SelendraNode.
pub type BlockNumber = u32;

/// Session index type
pub type SessionIndex = u32;

/// Era index type
pub type EraIndex = u32;

/// Session count type
pub type SessionCount = u32;

/// Version type for runtime versioning (finality version)
pub type Version = u32;
